writeSalaryInputForm();function writeSalaryInputForm(){document.getElementById("salaryForm").innerHTML="<label for=\"salaryInput\" class=\"form-label\">Please Enter a Yearly Salary (numbers only)</label><section class=\"input-group mb-3\"><span class=\"input-group-text\">$</span><input type=\"number\" class=\"form-control\" id=\"salaryInputBox\" name=\"salaryInputData\" aria-label=\"Yearly Salary Amount (Numbers Only)\" inputmode=\"decimal\" /></section><button type=\"button\" class=\"btn btn-success\" onclick=\"processSalaryData()\" id=\"enterSalaryButton\">Enter Salary Amount</button>";let salaryBox=document.getElementById("salaryInputBox");salaryBox.addEventListener("keypress",function(event){if(event.key==="Enter"){event.preventDefault();document.getElementById("enterSalaryButton").click()}})}function processSalaryData(){let salaryMinimum=72000;let salaryInput=document.getElementById("salaryInputBox").value;if(salaryInput<salaryMinimum){if(isNaN(salaryInput)||(salaryInput=="")||(salaryInput<1)){document.getElementById("salaryForm").innerHTML="<h2 class=\"text-danger border-bottom border-danger\"><i class=\"fa-duotone fa-traffic-light-stop me-2\" aria-hidden=\"true\"></i>Please Type a Number!</h2><p>Unfortunately, you did not type in a number. I will not accept any job offers that do not include an available salary!</p>"+writeRetryButton();return}let notEnoughMoney="<h2 class=\"text-warning border-bottom border-warning\"><i class=\"fa-duotone fa-traffic-light-slow me-2\" aria-hidden=\"true\"></i>Sorry</h2><p>Unfortunately, I can not accept "+dollarString(salaryInput)+" because that would mean taking a paycut. <span class=\"text-info\">Good luck in your candidate search</span> and I am not interested.</p>";if(salaryInput<10000){notEnoughMoney="<h2 class=\"text-danger border-bottom border-danger\"><i class=\"fa-duotone fa-traffic-light-stop me-2\" aria-hidden=\"true\"></i>Error Detected</h2><p>Did you enter an hourly amount by mistake? <strong class=\"text-info\">Please try again.</strong></p>"}document.getElementById("salaryForm").innerHTML=notEnoughMoney+writeRetryButton()}else{if((salaryInput<=salaryMinimum)||(hourlyToSalary(salaryInput)>salaryMinimum)){offerContactInfo(salaryInput)}}}function dollarString(num){const dollars=new Intl.NumberFormat("en-US",{currency:"USD",style:'currency'}).format(num);return dollars}function salaryToHourly(salary){let hourly=(salary/52)/40;return hourly}function hourlyToSalary(hourly){let salary=((hourly*8)*40)*52;return salary}function offerContactInfo(salaryAmount){document.getElementById("salaryForm").innerHTML="<h2 class=\"text-success border-bottom border-success\"><i class=\"fa-duotone fa-traffic-light-go me-2\" aria-hidden=\"true\"></i>Thank you for telling me how much this job is offering.</h2><p class=\"lead text-info fw-bold\">I will gladly accept <em class=\"text-success\">"+dollarString(salaryAmount)+"</em> as a yearly salary.</p><p>Please reach out to me via email at <a href=\"mailto:michael.ragsdale@raggiesoft.com\"><i class=\"fa-duotone fa-address-card me-1\" aria-hidden=\"true\"></i>michael.ragsdale@raggiesoft.com</a> so that we can discuss business.</p>"+writeRetryButton()}function writeRetryButton(){return "<p class=\"text-center\"><button type=\"button\" class=\"btn btn-success\" onclick=\"writeSalaryInputForm()\">Run This Again</button></p>"}